import axios from "axios";


const docesAPI = axios.create({ baseURL: "http://localhost:8000/vagas/" });

async function getDoces(){
    try {
        const response = await docesAPI.get('/');
        return response.data;
    } catch (error) {
        console.error("Erro ao buscar pedido na API ", error);
    }
}

async function encomendarDoces(doce) {
    try {							   		 
    const response = await docesAPI.get('/', doce)
	        return response.data;
    } catch (error) {
        console.error("Erro ao cadastrar doces na API ", error);
    }
} 

async function atualizarPedido(id, doces) {
    try {
        const response = await docesAPI.put(`/${id}`, doces);														   
    return response.data;
	} catch (error) {
        console.error(`Erro ao atualizar pedido com o ID ${id} na API `, error);
    }
}

async function excluirPedido(id) {
    try {
        const response = await docesAPI.delete(`/${id}`);
        return response.data;
    } catch (error) {
        console.error(`Erro ao excluir pedido com o ID ${id} na API `, error);
    }	 
}

export{
    getDoces,
	encomendarDoces,
	atualizarPedido,
	excluirPedido
};
